import React from 'react';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
} from 'reactstrap';


const MovieItem = ({ movies }) => {
    const URL_POSTER = "https://image.tmdb.org/t/p/w600_and_h900_bestv2"
    if (!movies){
        return <div>Loading...</div>
    }
    return (
        <div>
            <Card>
                <CardImg top width="100%" src={URL_POSTER + movies.poster_path} alt="Card image cap" />
                <CardBody>
                    <CardTitle>{movies.title}</CardTitle>
                    <CardText>{movies.overview}</CardText>
                </CardBody>
            </Card>
        </div>
    )
}

export default MovieItem
